import React from 'react';
import styled from "styled-components"

const StyledCard = styled.div`
    position: relative;
    width: 400px;
`;
const CardImage = styled.div`
    height: 400px;
    width: 100%;
    border-radius: 8px;
`;
const CardImg = styled.img`
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
    border-radius: inherit;
`;
const CardContent = styled.div`
    position: absolute;
    left: 50%;
    transform: translate(-50%, 0);
    bottom: 0;
    background-color: white;
    z-index: 10;
    border-radius: 20px;
    padding: 20px;
`;
const CardTop = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 30px;

`;
const CardUser = styled.div`
    display: flex;
    align-items: center;
    column-gap: 12px;
`;
const UserAvatar = styled.img`
    width: 30px;
    height: 30px;
    border-radius: 100rem;
    object-fit: cover;
    flex-shrink: 0;

`;
const Card = () => {
    return (
        <StyledCard>
            <CardImage>
                <CardImg 
                src="https://cdn.dribbble.com/users/2400293/screenshots/17389191/media/38c09e55fb949c8da8a4f56e9561923f.png?resize=1000x750&vertical=center"
                alt=""
                />
            </CardImage>
            <CardContent>
                <CardTop>
                    <CardUser>
                        <UserAvatar 
                            src='https://cdn.dribbble.com/users/2400293/screenshots/17389191/media/38c09e55fb949c8da8a4f56e9561923f.png?resize=1000x750&vertical=center'
                            alt=""
                        />
                        <span>@zndrson</span>
                    </CardUser>
                    <div>256</div>
                </CardTop>
                <div>
                    <h3>Cosmic Perspective</h3>
                    <span>12,000 PSL</span>
                </div>
            </CardContent>
        </StyledCard>
    );
};

export default Card;
